<?phpsession_start();header("Expires: Mon, 26 Jul 1970 05:00:00 GMT");header("Last-Modified: " . gmdate("D, d M Y H:i:s") . " GMT");header("Cache-Control: no-cache, must-revalidate");header("Pragma: no-cache");header("Content-type: text/html; charset=utf-8");require_once("../include/config.inc.php");include_once ("../include/address.mem.php");include_once ("../include/tools/FileUpload.php");checkAdminLogin(); // 同一账号不能同时登陆if( !isset($_SESSION['Oid']) || $_SESSION['Oid'] == "" || !isset($_SESSION['is_admin']) || $_SESSION['is_admin'] != ADMINLOGINFLAG ) {    echo "<script>alert('您的登录信息已过期,请重新登录!');top.location.href='/';</script>";    exit;}$id = $_REQUEST['id'];$uid = $_REQUEST["uid"];$langx = $_SESSION["langx"];$loginname = $_SESSION['UserName'];$type = $_REQUEST['type'];$now = date('Y-m-d H:i:s');$sql = "SELECT * FROM " . DBPREFIX . "web_carousel WHERE `id`=" . $id;$result = mysqli_query($dbLink, $sql);$row = mysqli_fetch_assoc($result);$categorySql = "SELECT `id`, `name`, `tag` FROM " . DBPREFIX . "web_picture_category WHERE status=1";$categoryResult = mysqli_query($dbLink, $categorySql);$categoryList = array();while ($categoryRow = mysqli_fetch_assoc($categoryResult)){    $categoryList[$categoryRow['id']] = $categoryRow;}// 轮播页面适用站点$aWebsites = explode(',', $row['website']); // 1：PC；2：Mobile；3：ADif($type == 'edit'){ // 基本记录设置    $title = trim($_POST['title']);    $cartype = isset($_REQUEST['cartype']) ? trim($_REQUEST['cartype']) : 'index'; //轮播种类标识    $categoryId = isset($_REQUEST['category_id']) ? $_REQUEST['category_id'] : 1; //默认轮播    $aWebsite = $_POST['website']; // 适用站点    $sWebsite = implode(',', $aWebsite);    $sitename = !empty($_REQUEST['sitename']) ? trim($_REQUEST['sitename']) : TPL_FILE_NAME;    $status = strval($_POST['status']);    $isShow = strval($_POST['is_show']);    $sequence = $_POST['sequence'];    $oldCarImg = $_POST['old_car_img'];    // 新旧版    $oCar = new FileUpload('car_url','/'.$sitename . '/carousels');    $car_url = $oCar->uploads($oldCarImg);    if(!$car_url){        $car_url = $oldCarImg;    }    if(!$car_url){        exit("<script> alert('" . $oCar->fileerror . "');history.back();</script>");    }    // 手机版    $oldCarImgMobile = $_POST['old_car_img_mobile'];    $oCarMobile = new FileUpload('car_url_mobile','/'.$sitename . '/carousels');    $car_url_mobile = $oCarMobile->uploads($oldCarImgMobile);    if(!$car_url_mobile){        $car_url_mobile = $oldCarImgMobile;    }    if(in_array(3, $aWebsite) && !$car_url_mobile){        exit("<script> alert('" . $oCarMobile->fileerror . "');history.back();</script>");    }    // 广告站    $oldCarImgAd = $_POST['old_car_img_ad'];    $oCarAd = new FileUpload('car_url_ad','/'.$sitename . '/carousels');    $car_url_ad = $oCarAd->uploads($oldCarImgAd);    if(!$car_url_ad){        $car_url_ad = $oldCarImgAd;    }    if(in_array(4, $aWebsite) && !$car_url_ad){        exit("<script> alert('" . $oCarAd->fileerror . "');history.back();</script>");    }    $sql = "UPDATE `" . DBPREFIX . "web_carousel` SET `title`='{$title}',`pic_url`='{$car_url}',`pic_url_mobile`='{$car_url_mobile}',            `pic_url_ad`='{$car_url_ad}',`cartype`='{$cartype}',`category_id`='{$categoryId}',`sitename`='{$sitename}',`website`='{$sWebsite}',            `status`='{$status}',`is_show`='{$isShow}',`sequence`='{$sequence}',`updated_at`='{$now}' WHERE `id` = {$id}";    $result = mysqli_query($dbMasterLink, $sql);    if($result){        refreshCarCache();        echo "<script> alert('更新成功！'); location.href='carousel.php?uid=$uid&lv=$lv&langx=$langx';</script>";    }else{        echo "<script> alert('更新失败！'); history.back();</script>";    }}// 更新轮播缓存function refreshCarCache(){    global $dbMasterLink;    $sql = "SELECT `id`, `title`, `pic_url`, `pic_url_mobile`, `pic_url_ad`, `cartype`,`category_id`, `sitename`, `website`,`status`,`sequence` FROM " . DBPREFIX . "web_carousel WHERE status = 1 AND `is_show`=1  order by sequence ASC limit 0,100";    $result = mysqli_query($dbMasterLink, $sql);    $data = array();    while ($row = mysqli_fetch_assoc($result)){        $data[]=$row;    }    $bannerPicSet = json_encode($data);    $redisObj = new Ciredis();    $redisObj->setOne('carouse_pic_set', $bannerPicSet);}?><html><head>    <title>main</title>    <meta http-equiv="Content-Type" content="text/html; charset=UTF-8 ">    <link rel="stylesheet" href="../../../style/agents/control_main.css?v=<?php echo AUTOVER; ?>" type="text/css"></head><body ><dl class="main-nav">    <dt>轮播单页管理</dt>    <dd></dd></dl><div class="main-ui all width_1000">    <form NAME="myFORM" action="carousel_edit.php?uid=<?php echo $uid?>&lv=<?php echo $lv?>&langx=<?php echo $langx?>" method="POST" enctype="multipart/form-data">        <INPUT TYPE=HIDDEN NAME="id" VALUE="<?php echo $id?>">        <INPUT TYPE=HIDDEN NAME="type" VALUE="edit">        <table  class="m_tab_ed">            <tr class="m_title_edit">                <td colspan="2" ><h4>轮播单页基本信息</h4></td>            </tr>            <tr class="m_bc_ed">                <td class="m_co_ed" width="140">轮播单页标题 :</td>                <td align="left" class="name_title">                    <input type="text" name="title" id="title" value="<?php echo $row['title']?>" size="50px" maxlength="50" required/>                    必填，活动主标题,且符合50字。                </td>            </tr>            <tr class="m_bc_ed">                <td class="m_co_ed" width="140">种类标识：</td>                <td align="left" class="name_title">                    <input type="text" name="cartype" id="cartype" value="<?php echo $row['cartype']?>" size="50px" maxlength="50" />                    <!--<select  name="cartype" id="cartype">                        <option value="">请选择类型</option>                        <?php /*foreach ($cartypeList as $key => $value) { */?>                            <option value="<?php /*echo $key;*/