             <input type=SUBMIT name="OK" value="确定" class="za_button">                    &nbsp; &nbsp; &nbsp;                    <input type=BUTTON name="FormsButton" value="取消" id="FormsButton" onClick="window.location.replace('carousel.php?uid=<?php echo $uid?>&lv=<?php echo $lv?>&langx=<?php echo $langx?>');" class="za_button">                </td>            </tr>        </table>    </form></div><script type="text/javascript" src="../../../js/agents/jquery.js"></script><script type="text/javascript" src="../../../js/agents/layer/layer.js"></script><script type="text/javascript">    function change(obj) {        var categoryId = $(obj).val();        if (categoryId == 7) { // 自动领取            $('.api_url').show();            $('.flag').show();        } else {            $('.api_url').hide();            $('.flag').hide();        }    }    function show_win(pic) {        var picUrl = '<?php echo HTTPS_HEAD.'://'.$_SERVER['HTTP_HOST'].'/uploads'; ?>' + pic;        var str = '<img src="'+picUrl+'" style="width: 100%;">';        layer.open({            type: 1,            title:'图片预览',            skin: 'layui-layer-gameplay',            anim: 2, // 动画风格            area: ['600px', '400px'],            content: str,            shadeClose: true,            shade: 0.5,            yes: function(){                layer.closeAll();            }        });    }</script></body></html>